import React, { Component } from 'react';
import './App.css';
import ListComponent from './components/ListComponent.js';


function TitleComponent() {
  return (
    <div className="Header">
      <h1>My List Invoice</h1>
    </div>
  )
}
class App extends Component {

  state = {
    data : []
  }

  componentDidMount() {
    this.getInvoices();
  }
  getInvoices() {
    const url = 'http://localhost:3000/Invoice';
    fetch(url)
      .then(response => response.json())
      .then(data => {
        this.setState({data});
        console.log(this.state.data)

      })
      .catch(error => {
        console.error(error);
      });
  }

  approved = _id => {
    const newInvoice = this.state.data.filter(invoice => invoice._id === _id);
    newInvoice[0].status = 'Approved';
    this.setState({ state : newInvoice })
  }
  render() {
    return (
      <div>
        <TitleComponent />
        <ListComponent stateList={this.state.data} approved={this.approved} />
      </div>
    );
  }


}


export default App;
