import React, { Component } from 'react';
import './App.css';
import ListComponent from './components/ListComponent.js';


function TitleComponent() {
  return (
    <div className="Header">
      <h1>My List Invoice</h1>
    </div>
  )
}
class App extends Component {

  stateList = []

  async getInvoices() {
    const res = await fetch('http://localhost:3000/Invoice');
    const data = await res.json();
    this.setState({ stateList: data })
  }

  approved = _id => {
    const newInvoice = this.stateList.filter(invoice => invoice._id === _id);
    this.setState({ stateList: newInvoice })
  }
  render() {
    return (
      <div>
        <TitleComponent />
        <ListComponent stateList={this.stateList} approved={this.approved} />
      </div>
    );
  }


}


export default App;
