import React, { Component } from 'react';
import './App.css';
import ListComponent from './components/ListComponent.js';


function TitleComponent() {
  return (
    <div className="Header">
      <h1>My List Invoice</h1>
    </div>
  )
}
class App extends Component {

  stateList = [
    {
      "_id": "5d73339830cfe60d38355ae3",
      "invoice_number": "12347",
      "total": "1229.99",
      "currency": "USD",
      "invoice_date": "2019-09-07T04:35:36.870Z",
      "due_date": "2019-09-07T04:35:36.870Z",
      "vendor_name": "Acme Cleaners Inc.",
      "remittance_address": "123 ABC St. Charlotte, NC 28209",
      "status": "Pending",
      "__v": 0
    },
    {
      "_id": "5d7334541832390d4dcc8356",
      "invoice_number": "12348",
      "total": "300.99",
      "currency": "USD",
      "invoice_date": "2019-09-07T04:38:44.479Z",
      "due_date": "2019-09-07T04:38:44.479Z",
      "vendor_name": "Acme Cleaners Inc.",
      "remittance_address": "123 ABC St. Charlotte, NC 28209",
      "status": "Pending",
      "__v": 0
    },
    {
      "_id": "5d7335112b256d0d670b1cd4",
      "invoice_number": "12348",
      "total": "300.99",
      "currency": "USD",
      "invoice_date": "2019-09-07T04:41:53.785Z",
      "due_date": "2019-09-07T04:41:53.785Z",
      "vendor_name": "Acme Cleaners Inc.",
      "remittance_address": "123 ABC St. Charlotte, NC 28209",
      "status": "Pending",
      "__v": 0
    }
  ]



  approved = _id => {
    const newInvoiceN = this.stateList.filter(invoice => invoice._id === _id);
    const newInvoice = this.stateList.map(invoice =>{
      if(invoice._id === _id){
        invoice.status = "Approved";
      }
      return invoice;
    });
    this.setState({stateList : newInvoiceN})
  }
  render() {
    return (
      <div>
        <TitleComponent />
        <ListComponent stateList={this.stateList} approved = { this.approved}/>
      </div>
    );
  }


}


export default App;
