import React, { Component } from 'react';
import './App.css';
import ListComponent from './components/ListComponent.js';
import socketIOClient from "socket.io-client";

function TitleComponent() {
  return (
    <div className="Header">
      <h1>My List Invoice</h1>
    </div>
  )
}
class App extends Component {

  constructor(props) {
    super(props);
    this.getInvoices = this.getInvoices.bind(this);
  }
  state = {
    data: []
  }
  componentDidMount() {
    var socket = socketIOClient("http://localhost:3000");
    socket.on('load', function (data) {
      this.getInvoices;
    });
    this.getInvoices();

  }

  getInvoices() {
    const url = 'http://localhost:3000/Invoice';
    fetch(url)
      .then(response => response.json())
      .then(data => {
        this.setState({ data });

      })
      .catch(error => {
        console.error(error);
      });
  }

  approvedInvoice(_id) {
    const url = 'http://localhost:3000/Invoice/' + _id;
    fetch(url, {
      method: 'POST', // or 'PUT'
      headers: {
        'Content-Type': 'application/json'
      }
    }).then(res => console.log(res))
      .catch(error => console.error('Error:', error))
      .then(response => console.log('Success:', response));
  }

  approvedBtn = _id => {
    const newInvoice = this.state.data.filter(invoice => invoice._id !== _id);
    this.approvedInvoice(_id);
    this.setState({ data: newInvoice })
  }

  render() {
    return (
      <div>
        <TitleComponent />
        <ListComponent stateList={this.state.data} approved={this.approvedBtn} />
      </div>
    );
  }


}


export default App;
